mapper.select:
select tfs.id, tfs.user_id as userId, tfs.title, tfs.introduce, tfs.pic0, tfs.pic1, tfs.pic2, tfs.pic3, tfs.lng, tfs.lat, tfs.address, tfs.site_phone as sitePhone, tfs.created, u.nick_name AS nickName, u.avatar_url AS avatarUrl, u.address AS address, tal.likeCount AS likeCount, tc.commentCount as commentCount, tcoll.user_id as isCollectioned, tlike.user_id as isLiked FROM tb_fish_shop tfs LEFT join (select source_id, source_type,count(*) as likeCount from tb_like GROUP BY source_id,source_type) tal on tal.source_id = tfs.id and tal.source_type=3 LEFT join (select topic_id, topic_type,count(*) as commentCount from tb_comment GROUP BY topic_id,topic_type) tc on tc.topic_id = tfs.id and tc.topic_type=3 LEFT join tb_user u on tfs.user_id = u.id LEFT join (select id,user_id,source_type,source_id from tb_collection GROUP BY id) tcoll on tcoll.user_id = #{userId} and tcoll.source_id = tfs.id and tcoll.source_type = 3 left join (select id,user_id,source_type,source_id from tb_like GROUP BY id ) tlike on tlike.user_id = #{userId} and tlike.source_id = tfs.id and tlike.source_type = 3
AND tfs.id = #{id} AND tfs.user_id = #{userId} AND tfs.publish_type = #{publishType} AND tfs.is_publish = #{isPublish}
AND tfs.ite_phone = #{sitePhone} AND tfs.yn = #{yn}
ORDER BY CONCAT('tfs.', '${orderField}', '') ${orderFieldType} LIMIT #{startIndex},#{pageSize}

		SELECT COUNT(tfs.id) AS dataCount  FROM tb_fish_shop tfs
			LEFT join (select source_id, source_type,count(*) as likeCount from tb_like GROUP BY source_id,source_type) tal on   tal.source_id = tfs.id and tal.source_type=3
 			LEFT join (select topic_id, topic_type,count(*) as commentCount from tb_comment GROUP BY topic_id,topic_type) tc on   tc.topic_id = tfs.id and tc.topic_type=3
			LEFT join tb_user u on tfs.user_id = u.id


			AND tfs.id = #{id}
			AND tfs.user_id = #{userId}
			AND tfs.publish_type = #{publishType}
			AND tfs.is_publish = #{isPublish}

			AND tfs.site_phone = #{sitePhone}
			AND tfs.yn = #{yn}
